mapper.select:
select id, user_id, report_id, description, step, equipment, post_time
from supplement s
where id = #{id}

select count(s.id)
from supplement s, task t,report p
where t.id = p.task_id and s.report_id = p.id and s.user_id = #{uid} and t.type = #{preference}

select count(s.id)
from supplement s, task t,report p
where t.id = p.task_id and s.report_id = p.id and s.user_id = #{uid} and t.equipment = #{equipment}
