mapper.update:
update user
set description = #{description}
where uid = #{uid}

update user
set preference = #{preference}
where uid = #{uid}

update user
set equipment = #{equipment}
where uid = #{uid}

update user
set similarity = #{similarity}
where uid = #{uid}

update user
set ability = #{ability}
where uid = #{uid}

update user
set evaluation = #{evaluation}
where uid = #{uid}

update user
set ability = ability+#{num}
where uid = #{uid}
